import React, { Component } from 'react';
// import './CommentArea.css';

class CommentArea extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text:'',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // handleChange(evt) {
  //   this.setState({
